import React from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        minHeight: "100vh",
        background: "linear-gradient(to right, #4facfe, #00f2fe)", // Improved gradient
        color: "#ffffff",
        fontFamily: "'Poppins', sans-serif",
        padding: "20px",
        textAlign: "center",
      }}
    >
      <h1
        style={{
          fontSize: "3rem",
          fontWeight: "bold",
          marginBottom: "20px",
          textShadow: "2px 2px 4px rgba(0, 0, 0, 0.4)", // Add subtle text shadow
        }}
      >
        Welcome to Your Career Portal
      </h1>
      <p
        style={{
          fontSize: "1.2rem",
          marginBottom: "30px",
          maxWidth: "600px",
          lineHeight: "1.5",
          color: "#e3f2fd", // Subtle contrast for secondary text
        }}
      >
        Unlock endless opportunities with our platform. Start exploring job
        listings and connect with leading companies to elevate your career.
      </p>
      <div
        style={{
          display: "flex",
          gap: "20px",
          flexWrap: "wrap",
          justifyContent: "center",
        }}
      >
        <button
          onClick={() => navigate("/job-listings")}
          style={{
            backgroundColor: "#ffffff",
            color: "#007bff",
            fontWeight: "bold",
            border: "none",
            padding: "15px 30px",
            fontSize: "1rem",
            borderRadius: "30px",
            cursor: "pointer",
            transition: "background-color 0.3s, color 0.3s",
            boxShadow: "0 4px 10px rgba(0, 0, 0, 0.3)",
          }}
          onMouseOver={(e) => {
            e.target.style.backgroundColor = "#007bff";
            e.target.style.color = "#ffffff";
          }}
          onMouseOut={(e) => {
            e.target.style.backgroundColor = "#ffffff";
            e.target.style.color = "#007bff";
          }}
        >
          Explore Jobs
        </button>
        <button
          onClick={() => navigate("/company-showcase")}
          style={{
            backgroundColor: "#ffffff",
            color: "#007bff",
            fontWeight: "bold",
            border: "none",
            padding: "15px 30px",
            fontSize: "1rem",
            borderRadius: "30px",
            cursor: "pointer",
            transition: "background-color 0.3s, color 0.3s",
            boxShadow: "0 4px 10px rgba(0, 0, 0, 0.3)",
          }}
          onMouseOver={(e) => {
            e.target.style.backgroundColor = "#007bff";
            e.target.style.color = "#ffffff";
          }}
          onMouseOut={(e) => {
            e.target.style.backgroundColor = "#ffffff";
            e.target.style.color = "#007bff";
          }}
        >
          View Companies
        </button>
      </div>
      <button
        onClick={handleLogout}
        style={{
          marginTop: "40px",
          backgroundColor: "rgba(255, 255, 255, 0.2)",
          color: "#ffffff",
          fontWeight: "bold",
          border: "2px solid #ffffff",
          padding: "15px 30px",
          fontSize: "1rem",
          borderRadius: "30px",
          cursor: "pointer",
          transition: "background-color 0.3s, color 0.3s",
        }}
        onMouseOver={(e) => {
          e.target.style.backgroundColor = "#ffffff";
          e.target.style.color = "#007bff";
        }}
        onMouseOut={(e) => {
          e.target.style.backgroundColor = "rgba(255, 255, 255, 0.2)";
          e.target.style.color = "#ffffff";
        }}
      >
        Logout
      </button>
    </div>
  );
};

export default Home;
