import React from "react";
import { AppBar, Toolbar, Button, Box } from "@mui/material";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const authToken = localStorage.getItem("authToken"); // Check if the user is authenticated
  const userType = localStorage.getItem("type"); // Get the user type (e.g., 'admin', 'employee')

  const handleLogout = () => {
    localStorage.clear(); // Clear all user data
    navigate("/login"); // Redirect to login page
  };

  return (
    <div>
      {/* Navbar */}
      <AppBar position="static" color="primary">
        <Toolbar style={{ justifyContent: "space-between" }}>
          {/* Admin-Specific Links */}
          {authToken && userType === "admin" && (
            <Box display="flex" gap="20px">
              <Button color="inherit" component={Link} to="/admin">
                Admin Panel
              </Button>
              <Button color="inherit" component={Link} to="/createjobpost">
                Job Post
              </Button>
            </Box>
          )}

          {/* Right Side - Logout */}
          <Box>
            {authToken ? (
              <Button color="inherit" onClick={handleLogout}>
                Logout
              </Button>
            ) : (
              <Button color="inherit" component={Link} to="/login">
                Login
              </Button>
            )}
          </Box>
        </Toolbar>
      </AppBar>
    </div>
  );
};

export default Navbar;
