import React from "react";
import { AppBar, Toolbar, Button, Typography, Box } from "@mui/material";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const authToken = localStorage.getItem("authToken"); // Check if the user is authenticated
  const userType = localStorage.getItem("type"); // Get the user type (e.g., 'admin', 'employee')

  const handleLogout = () => {
    localStorage.clear(); // Clear all user data
    navigate("/login"); // Redirect to login page
  };

  return (
    <div>
      {/* Navbar */}
      <AppBar position="static" color="primary">
        <Toolbar style={{ justifyContent: "space-between" }}>
          {/* Left Side Links */}
          <Box display="flex" gap="20px">
            <Button color="inherit" component={Link} to="/home">
              Home
            </Button>
            <Button color="inherit" component={Link} to="/about">
              About
            </Button>
            {authToken && userType === "employee" && (
              <Button color="inherit" component={Link} to="/job-listings">
                Job Listings
              </Button>
            )}
            {authToken && userType === "admin" && (
              <>
                <Button color="inherit" component={Link} to="/admin">
                  Admin Panel
                </Button>
                <Button color="inherit" component={Link} to="/createjobpost">
                  Job Post
                </Button>
              </>
            )}
            <Button color="inherit" component={Link} to="/contact">
              Contact
            </Button>
            <Button color="inherit" component={Link} to="/company-showcase">
              Company Showcase
            </Button>
          </Box>

          {/* Right Side - Login/Logout */}
          <Box>
            {authToken ? (
              <Button color="inherit" onClick={handleLogout}>
                Logout
              </Button>
            ) : (
              <Button color="inherit" component={Link} to="/login">
                Login
              </Button>
            )}
          </Box>
        </Toolbar>
      </AppBar>

      {/* Footer */}
      <Box
        component="footer"
        sx={{
          backgroundColor: "#1976d2",
          color: "white",
          textAlign: "center",
          padding: "10px 0",
          marginTop: "auto",
          position: "fixed",
          bottom: 0,
          width: "100%",
        }}
      >
        <Typography variant="body2">
          Â© {new Date().getFullYear()} Job Portal. All Rights Reserved.
        </Typography>
        <Typography variant="body2" sx={{ marginTop: "5px" }}>
          <a
            href="/terms"
            style={{ color: "white", textDecoration: "none", marginRight: "10px" }}
          >
            Terms of Service
          </a>
          |
          <a
            href="/privacy"
            style={{
              color: "white",
              textDecoration: "none",
              marginLeft: "10px",
            }}
          >
            Privacy Policy
          </a>
        </Typography>
      </Box>
    </div>
  );
};

export default Navbar;
